import styled from 'styled-components';
import { color } from '../../styles/styles';

export const Container = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  justify-content: center;

  background: ${color.lightest};
  height: 537px;
  width: 100%;

  h2 {
    font-size: 16px;
    font-weight: 700;
    margin: 0.5em 0;
    text-align: center;
  }
`;

export const Social = styled.div`
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: center;

  /* margin: 0.5em auto; */
  width: 100%;
  * {
    margin: 0 0.6em;
  }
`;

export const Inputs = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;

  p {
    font-size: 12px;
  }

  input {
    background-color: #f5f5f7 !important;
    margin: 0.5em;
  }
`;

export const Infos = styled.div`
  display: flex;
  align-items: center;
  flex-direction: row;
  justify-content: center;

  margin: 0.5em;

  div {
    display: flex;
    align-items: center;
    flex-direction: column;
    justify-content: space-between;

    height: 80px;
    width: 150px;

    h3 {
      font-size: 12px;
      font-weight: 700;
    }
  }
`;

export const Company = styled.div`
  display: flex;
  align-items: center;
  flex-direction: column;
  width: 275px;

  p {
    margin-bottom: 1em;
    margin-top: 0;
  }
`;

export const Contact = styled.p`
  font-size: 14px;
  text-align: left !important;
  width: 100%;
`;

export const Address = styled.p`
  font-size: 10px;
`;
