import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  flex-direction: column;
  width: 100%;
  height: 501px;
  background: linear-gradient(270deg, #19d9b4 -1.13%, #92d275 101.87%);
  .description {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    svg {
      width: 206px;
      height: 201px;
    }
    p {
      font-size: 32px;
      line-height: 48px;
      color: #ffffff;
      strong {
        font-weight: 600;
      }
    }
    .small {
      font-size: 20px;
      line-height: 30px;
      margin-top: 18px;
    }
  }
`;
