import styled, {
  css,
} from '../components/Home/SecondSection/node_modules/styled-components';

export const color = {
  // Palette
  primary: '#679C79', // coral
  secondary: '#FFFFFF', // ocean
  tertiary: '#DDDDDD',

  orange: '#F09536',
  gold: '#FFAE00',
  green: '#66BF3C',
  seafoam: '#37D5D3',
  purple: '#6F2CAC',
  ultraviolet: '#2A0481',

  // Monochrome
  lightest: '#FFFFFF',
  lighter: '#F8F8F8',
  light: '#F4F4F4',
  mediumlight: '#EEEEEE',
  medium: '#DDDDDD',
  mediumdark: '#999999',
  dark: '#666666',
  darker: '#444444',
  darkest: '#333333',

  border: 'rgba(0,0,0,.1)',

  // Status
  positive: '#66BF3C',
  negative: '#FF4400',
  warning: '#E69D00',

  // Text
  text: '#000',
};

export const typography = {
  type: {
    primary: '"Poppins", "Helvetica Neue", Helvetica, Arial, sans-serif',
    code:
      '"SFMono-Regular", Consolas, "Liberation Mono", Menlo, Courier, monospace',
  },
  weight: {
    regular: '500',
    bold: '700',
    extrabold: '800',
    black: '900',
  },
  size: {
    s1: 12,
    s2: 14,
    s3: 16,
    s4: 24,
    m1: 20,
    m2: 24,
    m3: 28,
    l1: 32,
    l2: 40,
    l3: 48,
    code: 90,
  },
};

export const Container = styled.div`
  display: flex;
  align-items: stretch;
  flex-direction: column;
  background: #fff;
  padding: 0 2em;

  h1 {
    font-size: 22px;
    font-weight: 700;
    margin: 0.5em;
  }
`;

export const Content = styled.div`
  display: flex;
  align-items: flex-start;
  flex-direction: column;
  margin: 0 0.5em;

  label {
    margin-top: 0.8em;
  }

  button {
    margin: 0.5em auto;
  }
`;
