import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  width: 100%;
  height: 338px;
  background: linear-gradient(
    270deg,
    #f09536 -1.13%,
    #f1b758 101.86%,
    #f09536 101.87%
  );
`;

export const Advertising = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  p {
    font-size: 32px;
    line-height: 48px;
    color: #ffffff;
    strong {
      font-weight: 600px;
    }
  }
  .btns {
    > button:first-child {
      background: linear-gradient(90.06deg, #92d275 0.06%, #19d9b4 99.95%);
      margin-right: 26px;
    }
    > button:last-child {
      color: #19d9b4;
    }
  }
`;
