import styled from 'styled-components';

export const Container = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: flex-start;
  align-items: center;
  padding: 17px 0 47px 0;
  width: 100%;
  height: 396px;
  background: linear-gradient(
    270deg,
    #efac86 -1.13%,
    #f1b758 101.86%,
    #f1b758 101.87%
  );
  h3 {
    font-weight: 600;
    font-size: 32px;
    line-height: 48px;
    text-align: center;
    color: #ffffff;
    margin-bottom: 0px;
  }
  p {
    font-size: 20px;
    line-height: 30px;
    text-align: center;
    color: #ffffff;
    margin: 0;
  }
`;

export const Items = styled.div`
  display: flex;
  justify-content: space-around;
  align-items: center;
  width: 100%;
  .item {
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
  }
`;
